function exportChart(o){$.post($("body").attr("hrefapp")+"/app/modules/dashboard/src/actions/exportGraph.php",{container:o}).done((function(p){$.when($("body").prepend(p)).then((function(){$("body").addClass("nblr"),$(".export-popup-act-pic").off("click").click((function(p){var t=$("#graph-"+o).find("canvas")[0].toDataURL("image/png");window.open("about:blank","Chart export").document.write("<img src='"+t+"' alt='Chart export'/>"),closeExportPopup()})),$(".export-popup").find("header").find("div").click((function(){closeExportPopup()})),$(".export-popup-act-csv").off("click").click((function(p){window.open($("body").attr("hrefapp")+"/app/modules/dashboard/src/actions/exportGraphAction.php?symbol="+$("#"+o).attr("symbol"),"_blank");closeExportPopup()}))}))})).fail((function(){showAlert("Oops","Fail to load export popup","error")}))}function closeExportPopup(){$(".export-popup").remove(),$("body").removeClass("nblr")}
